        super.onCreate(savedInstanceState)
        binding = PlayingFieldBinding.inflate(layoutInflater)
        setContentView(binding!!.getRoot())
        //Весь остальной код, выше не трогать!!!
        window.setFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS, WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS)
        if (!adIsShow)
        {
            mInterstitialAdLoader = InterstitialAdLoader(this@PlayingField)
            mInterstitialAdLoader!!.setAdLoadListener(object : InterstitialAdLoadListener
            {
                override fun onAdLoaded(interstitialAd: InterstitialAd)
                {
                    mInterstitialAd = interstitialAd
                    showAd()
                }

                override fun onAdFailedToLoad(error: AdRequestError)
                {
                }
            })
            loadInterstitialAd()
        }
        val show = AnimationUtils.loadAnimation(this, R.anim.show)
        val click = AnimationUtils.loadAnimation(this, R.anim.add_remove_player)
        scores = IntArray(Players.editTexts.size)
        currentPlayer = 0
        binding!!.currentPlayerTxt.text = "Выбирает\n${Players.editTexts[currentPlayer].text}"
        binding!!.trustShape.setOnClickListener {
            if (truths.size > 0)
            {
                val random = Random()
                val current = random.nextInt(truths.size)
                binding!!.currentQuestion.text = truths[current]
                binding!!.currentQuestion.startAnimation(show)
                truths.removeAt(current)
                hideAndShow(true)
                binding!!.currentPlayerTxt.text = "Отвечает\n${Players.editTexts[currentPlayer].text}"
                binding!!.currentPlayerTxt.startAnimation(show)
            }
            else
            {
                Toast.makeText(this@PlayingField, "Вопросы кончились!", Toast.LENGTH_SHORT).show()
            }
        }
        binding!!.dareShape.setOnClickListener {
            if (dares.size > 0)
            {
                val random = Random()
                val current = random.nextInt(dares.size)
                binding!!.currentQuestion.text = dares[current]
                binding!!.currentQuestion.startAnimation(show)
                dares.removeAt(current)
                hideAndShow(true)
                binding!!.currentPlayerTxt.text = """
                Действует
                ${Players.editTexts[currentPlayer].text}
                """.trimIndent()
                binding!!.currentPlayerTxt.startAnimation(show)
            }
            else
            {
                Toast.makeText(this@PlayingField, "Действия кончились!", Toast.LENGTH_SHORT).show()
            }
        }
        binding!!.yesButton.setOnClickListener {
            scores[currentPlayer]++
            hideAndShow(false)
            comlete()
        }
        binding!!.noButton.setOnClickListener {
            hideAndShow(false)
            comlete()
        }
        binding!!.settingsButton.setOnClickListener {
            val myIntent = Intent(this@PlayingField, Settings::class.java)
            startActivity(myIntent)
        }
        binding!!.shapeScore.setOnClickListener {
            binding!!.shapeScore.startAnimation(click)
            if (!onOff)
            {
                var score = "Счёт игры:\n\n"
                for (i in Players.editTexts.indices)
                {
                    score += Players.editTexts[i].text.toString() + ": " + scores[i] + "\n"
                }
                binding!!.scoreTextView.text = score
                binding!!.shapeScore.isEnabled = false
                binding!!.scoreCardView.visibility = View.VISIBLE
                binding!!.scoreCardView.animate().setDuration(1000).translationY(-600f).setListener(object : AnimatorListenerAdapter()
                    {
                        override fun onAnimationEnd(animation: Animator)
                        {
                            super.onAnimationEnd(animation)
                            binding!!.scoreCardView.translationY = -600f
                            onOff = true
                            binding!!.shapeScore.isEnabled = true
                        }
                    })
            }
            else
            {
                binding!!.shapeScore.isEnabled = false
                binding!!.scoreCardView.animate().setDuration(1000).translationY(600f).setListener(object : AnimatorListenerAdapter()
                    {
                        override fun onAnimationEnd(animation: Animator)
                        {
                            super.onAnimationEnd(animation)
                            binding!!.scoreCardView.translationY = 600f
                            binding!!.scoreCardView.visibility = View.VISIBLE
                            onOff = false
                            binding!!.shapeScore.isEnabled = true
                        }
                    })
            }
        }
    }

    @SuppressLint("SetTextI18n")
    private fun comlete()
    {
        val show = AnimationUtils.loadAnimation(this, R.anim.show)
        currentPlayer++
        if (Players.editTexts.size > currentPlayer)
        {
            binding!!.currentPlayerTxt.text = "Выбирает\n${Players.editTexts[currentPlayer].text}"
        }
        else
        {
            currentPlayer = 0
            binding!!.currentPlayerTxt.text = "Выбирает\n${Players.editTexts[currentPlayer].text}"
        }
        binding!!.currentPlayerTxt.startAnimation(show)
        binding!!.currentQuestion.text = getResources().getString(R.string.truth_or_dare)
        binding!!.currentQuestion.startAnimation(show)
    }

    private fun hideAndShow(state: Boolean)
    {
        val hide = AnimationUtils.loadAnimation(this, R.anim.hide)
        val show = AnimationUtils.loadAnimation(this, R.anim.show)
        if (state)
        {
            binding!!.trustShape.isEnabled = false
            binding!!.dareShape.isEnabled = false
            binding!!.truthButton.startAnimation(hide)
            binding!!.dareButton.startAnimation(hide)
            binding!!.truthButton.postDelayed({
                binding!!.dareButton.visibility = View.INVISIBLE
                binding!!.truthButton.visibility = View.INVISIBLE
            }, 500)
            binding!!.noButton.startAnimation(show)
            binding!!.yesButton.startAnimation(show)
            binding!!.yesButton.postDelayed({
                binding!!.yesButton.visibility = View.VISIBLE
                binding!!.noButton.visibility = View.VISIBLE
                binding!!.yesButton.isEnabled = true
                binding!!.noButton.isEnabled = true
            }, 500)
        }
        else
        {
            binding!!.yesButton.isEnabled = false
            binding!!.noButton.isEnabled = false
            binding!!.yesButton.startAnimation(hide)
            binding!!.noButton.startAnimation(hide)
            binding!!.yesButton.postDelayed({
                binding!!.yesButton.visibility = View.INVISIBLE
                binding!!.noButton.visibility = View.INVISIBLE
            }, 500)
            binding!!.truthButton.startAnimation(show)
            binding!!.dareButton.startAnimation(show)
            binding!!.dareButton.postDelayed({
                binding!!.dareButton.visibility = View.VISIBLE
                binding!!.truthButton.visibility = View.VISIBLE
                binding!!.trustShape.isEnabled = true
                binding!!.dareShape.isEnabled = true
            }, 500)
        }
    }

    override fun onKeyUp(keyCode: Int, event: KeyEvent): Boolean
    {
        return if (keyCode == KeyEvent.KEYCODE_BACK)
        {
            val dlgAlert = AlertDialog.Builder(this@PlayingField, R.style.MyDialogTheme)
            dlgAlert.setTitle("Подтвердите выход")
            dlgAlert.setMessage("При выходе, игра начнется заново!")
            dlgAlert.setCancelable(true)
            dlgAlert.setPositiveButton("ДА") { _: DialogInterface?, _: Int -> finishAffinity() }
            dlgAlert.setNegativeButton("НЕТ") { _: DialogInterface?, _: Int -> }
            val connDialog = dlgAlert.create()
            connDialog.setOnShowListener {
                val p = connDialog.getButton(AlertDialog.BUTTON_POSITIVE)
                p.setTextColor(getResources().getColor(R.color.black, this.theme))
                val n = connDialog.getButton(AlertDialog.BUTTON_NEGATIVE)
                n.setTextColor(getResources().getColor(R.color.black, this.theme))
            }
            connDialog.show()
            true
        }
        else
        {
            super.onKeyUp(keyCode, event)
        }
    }

    companion object
    {
        var adIsShow = false
        lateinit var scores: IntArray
        private var currentPlayer = 0
        private var onOff = false
    }
}
